import React from 'react';
import { Grid } from 'grommet';
import * as config from './config';
import useAppShell from './useAppShell';
import AppShellHeader from './AppShellHeader';
import AppShellMain from './AppShellMain';
import AppShellSidebar from './AppShellSidebar';

import './AppShell.css';

export default function AppShell({ children, ...props }) {
  const { authHandler, authLabel, user, userInitials } = useAppShell();

  return (
    <Grid
      className="AppShellContainer"
      rows={['xsmall', 'auto']}
      columns={['1/4', '1/4', '1/4', '1/4']}
      areas={[
        ['header', 'header', 'header', 'header'],
        ['sidebar', 'main', 'main', 'main'],
      ]}
      fill
    >
      <AppShellHeader
        authHandler={authHandler}
        authLabel={authLabel}
        logo={config?.site.logo}
        logoSmall={config?.site.logoSmall}
        userInitials={userInitials}
        userProfile={user?.profile}
        siteName={config?.site?.name}
      />
      <AppShellSidebar />
      <AppShellMain>{children}</AppShellMain>
    </Grid>
  );
}

import React from 'react';
import { Box, Grid, Header } from 'grommet';
import AppShellLogo from './AppShellLogo';
import AppShellSearch from './AppShellSearch';
import AppShellNotifications from './AppShellNotifications';
import AppShellMenu from './AppShellMenu';

export default function AppShellHeader({
  authHandler = () => {},
  authLabel,
  logo,
  logoSmall,
  searchHandler = () => {},
  siteName,
  userProfile,
  userInitials,
}) {
  return (
    <Header gridArea="header" className="AppShellHeader">
      <Grid
        columns={['1/4', '3/4']}
        rows={['auto']}
        areas={[['logo', 'menu']]}
        align="center"
        fill
      >
        <AppShellLogo
          text={siteName}
          logo={logo}
          logoSmall={logoSmall}
          height={{ max: '100%' }}
          width={{ max: '100%' }}
        />
        <Box
          gridArea="menu"
          direction="row"
          justify="end"
          pad="medium"
          gap="medium"
        >
          <AppShellSearch searchHandler={searchHandler} />
          <AppShellNotifications />
          <AppShellMenu
            authHandler={authHandler}
            authLabel={authLabel}
            userInitials={userInitials}
            userProfile={userProfile}
          />
        </Box>
      </Grid>
    </Header>
  );
}

import React, { useContext, useMemo } from 'react';
import { Box, Heading, Image, ResponsiveContext } from 'grommet';

export default function AppShellLogo({
  background = 'brand-3',
  text,
  logo,
  logoSmall,
  ...props
}) {
  const showText = !logo && text;
  const size = useContext(ResponsiveContext);
  const style = useMemo(() => {
    if (size === 'small') {
      return { maxHeight: '32px', maxWidth: '100%' };
    }
    return { maxHeight: '100%', maxWidth: '100%' };
  }, [size]);
  return (
    <Box
      className="AppShellLogo"
      background={background}
      gridArea="logo"
      pad="medium"
      justify="center"
      fill
      {...props}
    >
      {showText ? (
        <Heading level="3" textAlign="center" color="white">
          {text}
        </Heading>
      ) : (
        <Image
          src={size === 'small' ? logoSmall : logo}
          fit="contain"
          style={style}
        />
      )}
    </Box>
  );
}

import React from 'react';
import { Main } from 'grommet';

export default function AppShellMain({ children }) {
  return (
    <Main
      className="AppShellMain"
      gridArea="main"
      align="start"
      background="light-1"
      pad={{ horizontal: 'medium', vertical: 'medium' }}
      overflow={{ vertical: 'auto' }}
    >
      {children}
    </Main>
  );
}

import React from 'react';
import { Avatar, Box, DropButton, Text } from 'grommet';

export default function AppShellMenu({
  authHandler,
  authLabel,
  userInitials,
  userProfile,
}) {
  return (
    <DropButton
      className="AppShellMenu"
      icon={
        <Avatar background="brand" src={userProfile}>
          {userInitials}
        </Avatar>
      }
      dropAlign={{ top: 'bottom', right: 'right' }}
      dropContent={
        <Box pad="medium">
          <Text onClick={authHandler}>{authLabel}</Text>
        </Box>
      }
    />
  );
}

import React, { useContext, useMemo } from 'react';
import { NavLink as RouterNavLink } from 'react-router-dom';
import { useLocalStorage } from 'react-use';
import classNames from 'classnames';
import {
  Box,
  Collapsible,
  Nav,
  ResponsiveContext,
  Text,
  ThemeContext,
} from 'grommet';
import { Down, Up } from 'grommet-icons';

import * as config from './config';

function AppShellNavSection({ id, name, routes }) {
  return (
    <Box className="AppShellNavSection" gap="small">
      <Box pad={{ horizontal: 'medium' }}>
        <Text weight="bold">{name}</Text>
      </Box>
      <Box gap="none">
        {routes?.map((route) => (
          <AppShellNavItem key={route.id} {...route} />
        ))}
      </Box>
    </Box>
  );
}

function AppShellNavItem({
  id,
  path,
  icon,
  label,
  menuBackground = 'brand-2',
  routes,
  isNested = false,
  ...rest
}) {
  const size = useContext(ResponsiveContext);
  const [nav, setNav] = useLocalStorage('nav', {});
  const isExpanded = useMemo(() => {
    if (size === 'small') {
      return true;
    }

    return nav?.[id];
  }, [id, nav, size]);

  function toggleMenu() {
    setNav({
      ...nav,
      [id]: !nav?.[id],
    });
  }

  if (!routes) {
    return (
      <AppShellNavLink
        to={path}
        icon={icon}
        label={label}
        isNested={isNested}
        {...rest}
      />
    );
  }

  return (
    <Box gap="xsmall">
      <Box>
        <AppShellNavLink
          icon={icon}
          secondaryIcon={
            isExpanded ? <Up size="small" /> : <Down size="small" />
          }
          label={label}
          to={path}
          onClick={(e) => {
            e.preventDefault();
            toggleMenu();
          }}
          isNested={isNested}
          {...rest}
        />
      </Box>
      <Collapsible direction="vertical" open={isExpanded}>
        <Box gap="xsmall" background={menuBackground} fill="horizontal">
          {routes.map((route) => {
            return <AppShellNavItem key={route.id} {...route} isNested />;
          })}
        </Box>
      </Collapsible>
    </Box>
  );
}

function AppShellNavLink({
  activeBackground = 'brand-4',
  children,
  to,
  icon,
  secondaryIcon,
  label,
  color,
  size = 'large',
  weight = 'normal',
  onClick = (event) => {},
  isNested,
  ...props
}) {
  const theme = useContext(ThemeContext);
  const screenSize = useContext(ResponsiveContext);
  const className = classNames({
    AppShellNavLink: true,
    'is-small': screenSize === 'small',
  });
  return (
    <RouterNavLink
      className={className}
      to={to}
      style={{
        ...theme.anchor,
        fontWeight: 'normal',
        color: theme.global.colors['brand-contrast'],
      }}
      activeStyle={{
        ...theme.anchor,
        fontWeight: 'bold',
        color: theme.global.colors['brand-contrast'],
      }}
      onClick={onClick}
      exact
      strict
      {...props}
    >
      <Box
        direction="row"
        align="center"
        pad={{
          horizontal: 'medium',
          vertical: 'small',
        }}
        gap="medium"
      >
        {icon && (
          <Box className="NavLink__icon">
            <Text color={theme.global.colors['brand-contrast']}>{icon}</Text>
          </Box>
        )}

        {label && (
          <Box
            className="NavLink__label"
            margin={{ left: isNested ? 'medium' : 'none' }}
            flex
          >
            <Text
              color={color}
              size={size}
              margin={{ left: isNested ? 'medium' : 'none' }}
              truncate
            >
              {label}
            </Text>
          </Box>
        )}

        {secondaryIcon && (
          <Box className="NavLink__secondaryIcon">
            <Text color={theme.global.colors['brand-contrast']}>
              {secondaryIcon}
            </Text>
          </Box>
        )}
      </Box>
    </RouterNavLink>
  );
}

export default function AppShellNav() {
  return (
    <Nav className="AppShellNav" gap="medium">
      {config?.nav?.sections?.map((section) => (
        <AppShellNavSection key={section.id} {...section} />
      ))}
    </Nav>
  );
}

import React from 'react';
import {
  Avatar,
  Box,
  Card,
  CardBody,
  CardHeader,
  DropButton,
  Heading,
  Stack,
  Text,
} from 'grommet';
import useAppShell from './useAppShell';

function Icon({ icon, color = 'text', size = 'medium', ...props }) {
  const Component = icon;
  return (
    <Box {...props}>
      <Component color={color} size={size} />
    </Box>
  );
}

function AppShellNotification({ category, notifications = [] }) {
  return (
    <DropButton
      icon={
        <AppShellNotificationIndicator
          icon={category?.icon}
          count={notifications?.length}
        />
      }
      dropAlign={{ top: 'bottom', right: 'right' }}
      dropContent={
        <AppShellNotificationMenu
          category={category}
          notifications={notifications}
        />
      }
      dropProps={{
        plain: true,
        overflow: 'visible',
      }}
    />
  );
}

function AppShellNotificationIndicator({ icon, count }) {
  return (
    <Box>
      <Stack anchor="top-right">
        <Icon icon={icon} color="brand-3" />
        <Box
          align="center"
          justify="center"
          background="brand"
          pad={{ horizontal: 'xsmall' }}
          width={{ min: '18px' }}
          height={{ min: '18px' }}
          margin={{ top: '-7px', right: '-7px' }}
          round
        >
          <Text size="xsmall" color="brand-contrast">
            {count}
          </Text>
        </Box>
      </Stack>
    </Box>
  );
}

function AppShellNotificationMenu({ category, notifications }) {
  return (
    <Card width="medium">
      <CardHeader pad="small" background="white">
        <Heading level={5} margin="none">
          {category?.name}
        </Heading>
      </CardHeader>
      <CardBody pad="small" background="light-1">
        {notifications.map((notification) => {
          return (
            <AppShellNotificationMenuItem
              key={notification.id}
              {...notification}
            />
          );
        })}
      </CardBody>
    </Card>
  );
}

function AppShellNotificationMenuItem({
  description,
  id,
  image,
  metadata,
  title,
}) {
  return (
    <Box direction="row" align="start" gap="small">
      {image ? <Avatar src={image} /> : null}
      <Box gap="xsmall" flex>
        <Box direction="row" align="center" justify="between" gap="xsmall">
          {title ? (
            <Heading level={6} margin="none">
              {title}
            </Heading>
          ) : null}
          {metadata ? (
            <Text size="xsmall" color="dark-6">
              {metadata}
            </Text>
          ) : null}
        </Box>
        {description ? (
          <Text size="small" color="dark-3">
            {description}
          </Text>
        ) : null}
      </Box>
    </Box>
  );
}
export default function AppShellNotifications() {
  const { categories, notificationsByCategoryId } = useAppShell();
  return (
    <Box
      className="AppShellNotifications"
      direction="row"
      justify="end"
      pad="medium"
      gap="medium"
    >
      {categories.map((category) => {
        return (
          <AppShellNotification
            key={category?.id}
            category={category}
            notifications={notificationsByCategoryId[category?.id]}
          />
        );
      })}
    </Box>
  );
}

import React, { useEffect, useRef, useState } from 'react';
import { Box, DropButton, TextInput } from 'grommet';
import { Search } from 'grommet-icons';

export default function AppShellSearch({ searchHandler }) {
  const searchInput = useRef(null);
  const [isOpen, setIsOpen] = useState(false);
  useEffect(() => {
    if (isOpen) {
      setTimeout(() => searchInput.current.focus(), 0);
    }
  }, [isOpen]);
  return (
    <DropButton
      className="AppShellSearch"
      icon={<Search color="text" />}
      dropAlign={{ right: 'left' }}
      dropContent={
        <Box direction="row" pad={{ vertical: 'small' }}>
          <TextInput
            ref={searchInput}
            placeholder="Searchâ€¦"
            name="search"
            type="search"
          />
        </Box>
      }
      dropProps={{
        elevation: 'none',
      }}
      onClose={() => setIsOpen(false)}
      onOpen={() => setIsOpen(true)}
    />
  );
}

import React from 'react';
import { Box, Sidebar } from 'grommet';
import AppShellNav from './AppShellNav.js';

export default function AppShellSidebar({ background = 'brand-3' }) {
  return (
    <Sidebar
      className="AppShellSidebar"
      gridArea="sidebar"
      background={background}
      pad={{ horizontal: 'none', vertical: 'medium' }}
      overflow={{ vertical: 'auto' }}
    >
      <Box>
        <AppShellNav />
      </Box>
    </Sidebar>
  );
}

import { useMemo, useState } from 'react';
import * as config from './config';
import { chain } from 'lodash';

export default function useAppShell() {
  const [isAuthenticated, setIsAuthenticated] = useState(true);
  const [user, setUser] = useState(config?.user);
  const [notifications] = useState(config?.notifications);
  const [categories] = useState(config?.categories);

  const authLabel = useMemo(() => {
    return isAuthenticated ? 'logout' : 'login';
  }, [isAuthenticated]);

  const notificationsByCategoryId = useMemo(
    () => chain(notifications).groupBy('categoryId').value(),
    [notifications]
  );

  const userInitials = useMemo(() => {
    if (user) {
      return `${user?.firstName[0]}${user?.lastName[0]}`;
    }

    return null;
  }, [user]);

  return useMemo(() => {
    function authHandler() {
      if (isAuthenticated) {
        logout();
      } else {
        login();
      }
    }

    function login() {
      setIsAuthenticated(true);
      setUser(config?.user);
    }

    function logout() {
      setIsAuthenticated(false);
      setUser(null);
    }

    return {
      authHandler,
      authLabel,
      isAuthenticated,
      login,
      logout,
      notifications,
      notificationsByCategoryId,
      categories,
      user,
      userInitials,
    };
  }, [
    authLabel,
    isAuthenticated,
    notifications,
    notificationsByCategoryId,
    categories,
    user,
    userInitials,
  ]);
}
