import React, { lazy, Suspense } from 'react';
import { Route, Switch, useRouteMatch } from 'react-router-dom';

export default function Router() {
  const Cart = lazy(() => import('./Cart'));
  const Checkout = lazy(() => import('./Checkout'));
  const Store = lazy(() => import('./Store'));
  const { path } = useRouteMatch();

  return (
    <Suspense fallback={<p>loading routeâ€¦</p>}>
      <Switch>
        <Route component={Cart} path={`${path}/cart`} />
        <Route component={Checkout} path={`${path}/checkout`} />
        <Route component={Store} path={`${path}/store`} />
      </Switch>
    </Suspense>
  );
}

import { useCallback, useMemo, useState } from 'react';
import accounting from 'accounting';
import * as config from './config';

export default function useCart() {
  const [products, setProducts] = useState(config?.products);

  const lineItems = useMemo(() => {
    return products.map((product) => {
      const total = product.quantity * product.price;
      return {
        ...product,
        priceLabel: accounting.formatMoney(product.price),
        total,
        totalLabel: accounting.formatMoney(total),
      };
    });
  }, [products]);

  const total = useMemo(() => {
    return lineItems.reduce((accum, lineItem) => accum + lineItem.total, 0);
  }, [lineItems]);

  const totalLabel = useMemo(() => {
    return accounting.formatMoney(total);
  }, [total]);

  const removeProduct = useCallback(
    (id) => {
      setProducts(products.filter((product) => product.id !== id));
    },
    [products]
  );
  const updateProduct = useCallback(
    (payload) => {
      setProducts(
        products.map((product) => {
          if (product.id !== payload.id) {
            return product;
          }

          return {
            ...product,
            ...payload,
          };
        })
      );
    },
    [products]
  );

  return useMemo(() => {
    return {
      lineItems,
      products,
      total,
      totalLabel,
      updateProduct,
      removeProduct,
    };
  }, [lineItems, products, total, totalLabel, updateProduct, removeProduct]);
}

import { useCallback, useMemo, useState } from 'react';
